$(document).ready(function() {
    // Inisialisasi DataTable dasar tanpa pengaturan khusus
    $(".rohman-syah").DataTable();

    // Inisialisasi DataTable dengan pengurutan default pada kolom ke-3 (indeks 3) secara menurun
    $(".default-ordering").DataTable({
        order: [[3, "desc"]]
    });

    // Inisialisasi DataTable dengan pengurutan multi-kolom
    $(".multi-ordering").DataTable({
        columnDefs: [
            { targets: 0, orderData: [0, 1] },
            { targets: 1, orderData: [1, 0] },
            { targets: 4, orderData: [4, 0] }
        ]
    });

    // Inisialisasi DataTable untuk header kompleks
    $(".complex-headers").DataTable();

    // Inisialisasi DataTable dengan posisi DOM khusus
    $(".dom-positioning").DataTable({
        dom: '<"top"i>rt<"bottom"flp><"clear">'
    });

    // Inisialisasi DataTable dengan tipe paginasi lengkap
    $(".alt-pagination").DataTable({
        pagingType: "full_numbers"
    });

    // Inisialisasi DataTable dengan scroll vertikal
    $(".scroll-vertical").DataTable({
        scrollY: "200px",
        scrollCollapse: true,
        paging: false
    });

    // Inisialisasi DataTable dengan scroll dinamis berdasarkan tinggi viewport
    $(".dynamic-height").DataTable({
        scrollY: "50vh",
        scrollCollapse: true,
        paging: false
    });

    // Inisialisasi DataTable dengan scroll horizontal
    $(".scroll-horizontal").DataTable({
        scrollX: true
    });

    // Inisialisasi DataTable dengan scroll horizontal dan vertikal
    $(".scroll-horizontal-vertical").DataTable({
        scrollY: 200,
        scrollX: true
    });

    // Inisialisasi DataTable dengan format angka kustom
    $(".comma-decimal-place").DataTable({
        language: {
            decimal: ",",
            thousands: "."
        }
    });
});
